    case "up":
        if (yPos > 0) {
            yPos = yPos - 1;
        }
        break;
    case "down":
        if (yPos < 6 ) {
            yPos = yPos + 1;
        }
        break;
    case "left":
        if (xPos > 0) {
            xPos = xPos - 1;
        }
        break;
    case "right":
        if (xPos < 6) {
            xPos = xPos + 1;
        }
        break;
    case "off":
        xPos = 0;
        yPos = 0;
        eye = off;
        global.set("eyeShow", false);
        break;
    case "start":
        xPos = 3;
        yPos = 3;
        global.set("eyeShow", true);
        break; 
    case "wink":
        break;
}

